using Microsoft.FSharp.Collections;

namespace cuddly_lamp.Input;
public class Input6 : AbstractInput<(FSharpSet<Tuple<int, int>>, int, int, Tuple<int, int>), (FSharpSet<Tuple<int, int>>, int, int, Tuple<int, int>)>
{
    protected override (FSharpSet<Tuple<int, int>>, int, int, Tuple<int, int>) FormatFirst(string input)
    {
        var rows = input.Split('\n');
        var y = rows.Length;
        var x = rows[0].Length;
        Tuple<int, int> guard = (-1, -1).ToTuple();
        var map = new HashSet<(int, int)>();
        for (var i = 0; i < y; i++)
        {
            for (var j = 0; j < x; j++)
            {
                if (rows[i][j] == '#')
                {
                    map.Add((j, i));
                }
                else if (rows[i][j] == '^')
                {
                    guard = (j, i).ToTuple();
                }
            }
        }

        return (new FSharpSet<Tuple<int, int>>(map.Select(p => p.ToTuple())), x, y, guard);
    }

    protected override (FSharpSet<Tuple<int, int>>, int, int, Tuple<int, int>) FormatSecond(string input)
    {
        throw new NotImplementedException();
    }

